import React, {useState, useEffect} from 'react'
import '../adminDb.css'
import { getAllPatients, getAllDoctors, getAllAppointments } from '../api';


const Doctorsdb = () => {
  const [patients, setPatients] = useState([])

  const [appointments, setAppointments] = useState([])

  const [doctors, setDoctors] = useState([])

  useEffect(() => {
    const fetchDoctors = async () => {
      await getAllDoctors().then((res) => {
        setDoctors(res)
      }).catch((e) => {
        console.log(e)
      })
    }

   fetchDoctors()
  }, [])

  useEffect(() => {
    const fetchPatients = async () => {
      await getAllPatients().then((res) => {
        setPatients(res)
      }).catch((e) => {
        console.log(e)
      })
    }

   fetchPatients()
  }, [])

  useEffect(() => {
    const fetchAppointments = async () => {
      await getAllAppointments().then((res) => {
        setAppointments(res)
      }).catch((e) => {
        console.log(e)
      })
    }

   fetchAppointments()
  }, [])

  return (
    <div className='container'>
      <table>
        <thead>
          <tr>
            <th>id</th>
            <th>Patient name</th>
            <th>Doctor name</th>
            <th>Appointment description</th>
            <th>Appointment time</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
         {
          patients && patients.map(({id, firstName}) => (
            <tr key={id}>
              <td>{id}</td>
              <td>{firstName}</td>
            </tr>
          ))

         }
         {
          doctors && doctors.map(({id, firstName}) => (
            <tr key={id}>
              <td>{firstName}</td>
            </tr>
          ))
         }

         {
          appointments && appointments.map(({id, description, time}) => (
            <tr key={id}>
              <td>{description}</td>
              <td>{time}</td>
            </tr>
          ))
         }
          
        </tbody>
      </table>
    </div>
  );
};

export default Doctorsdb;
