import React, {useState, useEffect} from 'react'
import '../adminDb.css'
import { getAllPatients, getAllDoctors, getAllAppointments } from '../api';


const Doctorsdb = () => {
  const [patients, setPatients] = useState([])

  const [appointments, setAppointments] = useState([])

  const [doctors, setDoctors] = useState([])

  useEffect(() => {
    const fetchDoctors = async () => {
      await getAllDoctors().then((res) => {
        setDoctors(res)
      }).catch((e) => {
        console.log(e)
      })
    }

   fetchDoctors()
  }, [])

  useEffect(() => {
    const fetchPatients = async () => {
      await getAllPatients().then((res) => {
        setPatients(res)
      }).catch((e) => {
        console.log(e)
      })
    }

   fetchPatients()
  }, [])

  useEffect(() => {
    const fetchAppointments = async () => {
      await getAllAppointments().then((res) => {
        setAppointments(res)
      }).catch((e) => {
        console.log(e)
      })
    }

   fetchAppointments()
  }, [])

  

  

  return (
    <div className='container'>
      <table>
        
        <thead>
          <tr>
            <th>Patient Id</th>
            <th>Patient name</th>
            <th>Doctors name</th>
            <th>Doctors name</th>
            <th>Appointment Details</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
        
        {
          patients && patients.map(({id, firstName}) => (
            <tr key={id}>
              <td>{id}</td>
              <td>{firstName}</td>
              <td>
              {
          doctors && doctors.map(({id, firstName}) => (
            <tr key={id}>
              <td>{firstName}</td>
            </tr>
          ))

         }
              </td>
              <td>
              {
          doctors && doctors.map(({id, doctorName}) => (
            <tr key={id}>
              <td>{doctorName}</td>
            </tr>
          ))

         }

              </td>

              <td>
              {
          appointments && appointments.map(({id, appointmentDetails, appointmentDate}) => (
            <tr key={id}>
              <td>{appointmentDetails}</td>
              <td>{appointmentDate}</td>
            </tr>
          ))
         }

              </td>
            </tr>
          ))
        } 
          

         
         
         

        


        </tbody>
      </table>
    </div>
  );
};

export default Doctorsdb;
