import React, {useState, useEffect} from 'react'
import '../adminDb.css'
import { getAllPatients, getAllDoctors, getAllAppointments } from '../api';


const Doctorsdb = () => {
  const [patients, setPatients] = useState([])

  const [appointments, setAppointments] = useState([])

  const [doctors, setDoctors] = useState([])

  useEffect(() => {
    const fetchDoctors = async () => {
      await getAllDoctors().then((res) => {
        setDoctors(res)
      }).catch((e) => {
        console.log(e)
      })
    }

   fetchDoctors()
  }, [])

  useEffect(() => {
    const fetchPatients = async () => {
      await getAllPatients().then((res) => {
        setPatients(res)
      }).catch((e) => {
        console.log(e)
      })
    }

   fetchPatients()
  }, [])

  useEffect(() => {
    const fetchAppointments = async () => {
      await getAllAppointments().then((res) => {
        setAppointments(res)
      }).catch((e) => {
        console.log(e)
      })
    }

   fetchAppointments()
  }, [])

  const data = [...doctors, ...patients, ...appointments]
  console.log(data)

  

  return (
    <div className='container'>
      <table>
        
        <thead>
          <tr>
            <th>Patient name</th>
            <th>Doctor name</th>
            <th>Appointment description</th>
            <th>Appointment time</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
       
{data && data.map(({ id, firstName, doctorName, appointmentDetails, appointmentDate, approval }) => (
            <tr key={id}>
              <td>{firstName}</td>
              <td>{doctorName}</td>
              <td>{appointmentDetails}</td>
              <td>{appointmentDate}</td>
              <td>{approval}</td>
            </tr>
          ))}
          
          

         
          
        </tbody>
      </table>
    </div>
  );
};

export default Doctorsdb;
