import React, {useState} from 'react';
import '../userLogin.css';
import {useNavigate} from 'react-router-dom';

function Registration(){
  const [firstName,setFirstName]=useState("")
  const [lastName,setLastName]=useState("")
  const [username,setUserName]=useState("")
  const [email,setEmail]=useState("")
  const [dateOfBirth,setDateOfBirth]=useState("")
  const [password,setPassword]=useState("")
  const navigate=useNavigate()

  async function signUp()
  {
    let item = {firstName,lastName,username,email,dateOfBirth,password}
    console.warn(item)

    let result = await fetch("http://localhost:8080/api/auth/register",{
      method:'POST',
      body:JSON.stringify(item),
      headers:{
        "Content-Type": 'application/json',
        "Accept": 'application/json'
      }
    })
    result=  await result.jason()
    localStorage.setItem("user-infor",JSON.stringify(result))
    navigate.push("/")
  }

  return(
    <div className='loginb'>
      <form>
        <div className='form'>
        <h1>Register</h1>
        <input type="text" value={firstName} onChange={(e)=>setFirstName(e.target.value)} className='form-control'placeholder='enter firstName..'/>
        <br />
        <input type='text' value={lastName} onChange={(e)=>setLastName(e.target.value)} className='form-control'placeholder='enter lastName..'/>
        <br />
        <input type='text' value={username}  onChange={(e)=>setUserName(e.target.value)} className='form-control'placeholder='enter username..'/>
        <br />
        <input type='email'value={email} onChange={(e)=>setEmail(e.target.value)} className='form-control'placeholder='enter email..'/>
        <br />
        <input type='dateTime-local' value={dateOfBirth} onChange={(e)=>setDateOfBirth(e.target.value)} className='form-control'placeholder='enter DateOfBirth'/>
        <br />
        <input type='password' value={password} onChange={(e)=>setPassword(e.target.value)} className='form-control'placeholder='enter password.. '/>
        <br />
        <button onClick={signUp} className="btn">signUp</button>
        </div>
      </form>

    </div>
  )
}

export default Registration
