import React from 'react';
import '../userLogin.css';
import {useNavigate} from 'react-router-dom';
import { useMutation } from "react-query";
import { toast } from "react-toastify";
import {loginUser} from "../api/authapi";
import { useState, useEffect } from 'react';
import useAuth from '../hooks/useAuth';




function UserLogin() {
  const {setAuth} = useAuth();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate()
  useEffect(()=> {

    if(localStorage.getItem("cookie")) {
       navigate.push("/finalDb")
    }

  }, [])
  async function login() {

    let item = {
      email,
      password
    };

    let result = await fetch("http://localhost:8080/api/auth/login", {
      method:'POST',
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"

      },
      body: JSON.stringify(item)
    });

    result = await result.json();

    const cookie = JSON.stringify(result)

    const values = cookie.split(" ")

    const userId = values[0]
    const roleId = values[1]

    setAuth({email, cookie, roleId})

    localStorage.setItem("cookie",userId + roleId)

    navigate.push("/finalDb")
    
  }
    return (
      <div className='loginb' >
       <form onSubmit={login}>
   

  <div className="form" >
    <h1>Hospital appointment module</h1><br/>
    <h1>User Login</h1>
    <input type="email" 
     placeholder='Enter your email...'
     value={email}
     onChange={(e) => setEmail(e.target.value)}
      className="form-control" 
      required='required'/>
    <br/>
    
    
    
   
 
  <br/>

 
  <div className="form-outline mb-4">
    <input type="password" 
    placeholder='Enter your password...'
    value={password}
    onChange={(e) => setPassword(e.target.value)} 
    className="form-control" 
    required='required'/>
    
  </div>
  <br/>

 
  <div className="row mb-4">
    <div className="col d-flex justify-content-center">
      
      <div className="form-check">
        <input className="form-check-input" type="checkbox" value="" id="form2Example31"  />
        
        <label className="form-check-label" for="form2Example31"> Login as Admin </label>
      </div>
    </div>
    <br/>

    <div className="col">
    
    <p className = 'kfc'><a href="#!">Forgot password?</a>
       Or  <a href="/addPatients">Create account</a></p>
    </div>
  </div>
  
  <div className="text-center">
   


  <button type="submit" className="btn">Login</button>
  <br/>
  </div>

  
  
    
    
    

  </div>
</form>
</div>

      
    );
  };
  
  export default UserLogin;
